import React, { useState } from 'react';
import Navbar from './Navbar';

function Home() {
  const [location, setLocation] = useState('all');
  const [priceRange, setPriceRange] = useState('all');
  const [accommodationType, setAccommodationType] = useState('all');

  const handleSearch = (e) => {
    e.preventDefault();
    console.log('Search query:', location, priceRange, accommodationType);
    // Handle search query logic here
  };

  return (
    <div style={{
      backgroundImage: `url(${process.env.PUBLIC_URL}/background.jpeg)`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      height: '100vh',
      width: '100vw'
    }}>
      <Navbar />
      <main>
        <h1>Welcome to Westland Properties</h1>
        <p>Find your dream home with us!</p>
        <div className="search-bar">
          <form onSubmit={handleSearch}>
            <div className="search-dropdowns">
              <select
                id="location"
                name="location"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
              >
                <option value="all">All Locations</option>
                <option value="new-york">New York</option>
                <option value="los-angeles">Los Angeles</option>
                <option value="chicago">Chicago</option>
              </select>
              <select
                id="price-range"
                name="price-range"
                value={priceRange}
                onChange={(e) => setPriceRange(e.target.value)}
              >
                <option value="all">All Price Ranges</option>
                <option value="60-100">$60 - $100</option>
                <option value="110-130">$110 - $130</option>
                <option value="150-200">$150 - $200</option>
              </select>
              <select
                id="accommodation-type"
                name="accommodation-type"
                value={accommodationType}
                onChange={(e) => setAccommodationType(e.target.value)}
              >
                <option value="all">All Types</option>
                <option value="room2">Room 2</option>
                <option value="room3">Room 3</option>
                <option value="above3">above 3</option>
              </select>
              <button id="search-btn" type="submit">
                Search
              </button>
            </div>
          </form>
        </div>
        <button>Explore Listings</button>
      </main>
      <footer>
        <p>&copy; 2024 Westland Properties</p>
        <p>Contact Us: <a href="mailto:info@westlandproperties.com">info@westlandproperties.com</a></p>
        <p>Phone: +263 78 904 7833</p>
        <p>Address: 09 Qourn Ave Mt Pleasent, Hre</p>
      </footer>
    </div>
  );
}

export default Home;
