import React, { useState } from 'react';

function SignupForm() {
  const [formData, setFormData] = useState({
    accountType: '',
  });

  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      const { accountType } = formData;

      if (accountType === '') {
        setError('Please select an account type');
        return;
      }

      if (accountType === 'admin') {
        // Handle admin sign-up logic
        console.log('Admin sign-up');
      } else if (accountType === 'student') {
        // Handle student sign-up logic
        console.log('Student sign-up');
      }
    } catch (error) {
      console.error('Sign-up error:', error);
      setError('An error occurred. Please try again later.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div>
      <h1>Westland Accommodation</h1>
      <h2>Sign up</h2>
      <form onSubmit={handleSubmit}>
        <label>
          <input
            type="text"
            placeholder="Username"
            style={{ marginBottom: '20px' }}
          />
        </label>
        <br />
        <label>
          <input type="email" placeholder="Email" style={{ marginBottom: '20px' }} />
        </label>
        <br />
        <label>
          <input type="password" placeholder="Password" style={{ marginBottom: '20px' }} />
        </label>
        <br />
        <label>
          <input
            type="password"
            placeholder="Confirm Password"
            style={{ marginBottom: '20px' }}
          />
        </label>
        <br />
        <label>
          Account Type:
          <select name="accountType" value={formData.accountType} onChange={handleChange}>
            <option value="">Select an account type</option>
            <option value="admin">Admin</option>
            <option value="student">Student</option>
          </select>
        </label>
        <br />
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {isLoading ? (
          <p>Loading...</p>
        ) : (
          <button type="submit">Sign up</button>
        )}
        <p>
          Already have an account? <a href="/login">Login</a>
        </p>
      </form>
    </div>
  );
}

export default SignupForm;
