import React, { useState } from 'react';
import axios from 'axios';

function FileUpload() {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [error, setError] = useState(null);
  const [usePricing, setUsePricing] = useState('');
  const [location, setLocation] = useState('');
  const [availableRooms, setAvailableRooms] = useState('');
  const [servicesProvided, setServicesProvided] = useState('');

  const handleFileChange = (event) => {
    setSelectedFiles(event.target.files);
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    switch (name) {
      case 'usePricing':
        setUsePricing(value);
        break;
      case 'location':
        setLocation(value);
        break;
      case 'availableRooms':
        setAvailableRooms(value);
        break;
      case 'servicesProvided':
        setServicesProvided(value);
        break;
      default:
        break;
    }
  };

  const handleFileUpload = async () => {
    const formData = new FormData();
    for (let i = 0; i < selectedFiles.length; i++) {
      formData.append('files', selectedFiles[i]);
    }
    formData.append('usePricing', usePricing);
    formData.append('location', location);
    formData.append('availableRooms', availableRooms);
    formData.append('servicesProvided', servicesProvided);

    try {
      const response = await axios.post('/api/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        onUploadProgress: (progressEvent) => {
          const progress = Math.round(
            (progressEvent.loaded * 100) / progressEvent.total
          );
          setUploadProgress(progress);
        },
      });

      if (response.status === 200) {
        console.log('Files uploaded successfully!');
        // Handle success, e.g., display a success message
      } else {
        setError('Error uploading files');
      }
    } catch (error) {
      console.error('Error uploading files:', error);
      setError('Error uploading files. Please try again later.');
    } finally {
      setUploadProgress(0);
    }
  };

  return (
    <div className="file-upload-container">
      <h1>File Upload</h1>
      <input type="file" multiple onChange={handleFileChange} />
      <button onClick={handleFileUpload}>Upload Files</button>
      <div className="form-group">
        <label>Use Pricing:</label>
        <input
          type="text"
          name="usePricing"
          value={usePricing}
          onChange={handleInputChange}
        />
      </div>
      <div className="form-group">
        <label>Location:</label>
        <input
          type="text"
          name="location"
          value={location}
          onChange={handleInputChange}
        />
      </div>
      <div className="form-group">
        <label>Available Rooms:</label>
        <input
          type="text"
          name="availableRooms"
          value={availableRooms}
          onChange={handleInputChange}
        />
      </div>
      <div className="form-group">
        <label>Services Provided:</label>
        <textarea
          name="servicesProvided"
          value={servicesProvided}
          onChange={handleInputChange}
        />
      </div>
      {uploadProgress > 0 && (
        <div className="progress-container">
          <progress value={uploadProgress} max="100" />
          <p>{uploadProgress}% uploaded</p>
        </div>
      )}
      {error && <p className="error-message">{error}</p>}
    </div>
  );
}

export default FileUpload;
